IF EXISTS (SELECT * FROM sys.objects WHERE object_id = OBJECT_ID(N'[dbo].[rptTagList]') AND type in (N'P', N'PC')) DROP PROCEDURE [dbo].[rptTagList]
EXEC dbo.sp_executesql @statement = N'-- ============================================= -- Author: Bret Cline -- Create date: 7/8/2011 -- Description: Tag List Report for Bev Manager -- ============================================= CREATE PROCEDURE [dbo].[rptTagList] AS BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
SELECT T.TagNumber , dbo.ConvertVolume(I.Amount) , dbo.[NozzleDiameter]( COALESCE( N.Length, UN.Length, CN.Length, RN.Length, SN.Length) , COALESCE( N.Width, UN.Width, CN.Width, RN.Width, SN.Width ) , COALESCE( N.Shape, UN.Shape, CN.Shape, RN.Shape, SN.Shape ) ) AS NozzleArea , L.Name , U.Name , M.Name AS Manufacturer FROM Tags T JOIN Inventories I ON T.TagID = I.TagID JOIN Locations L ON T.LocationID = L.LocationID JOIN UPCs U ON I.UPCID = U.UPCID JOIN Manufacturers M ON U.ManufacturerID = M.ManufacturerID LEFT JOIN dbo.Categories AS C ON U.CategoryID = C.CategoryID LEFT JOIN dbo.Categories AS R ON U.RootCategoryID = R.CategoryID LEFT JOIN StandardNozzles N ON T.StandardNozzleID = N.StandardNozzleID LEFT JOIN StandardNozzles UN ON T.StandardNozzleID = UN.StandardNozzleID LEFT JOIN StandardNozzles CN ON C.StandardNozzleID = CN.StandardNozzleID LEFT JOIN StandardNozzles RN ON R.StandardNozzleID = RN.StandardNozzleID , StandardNozzles SN WHERE SN.StandardNozzleID = ''00000000-0000-0000-0000-000000000000'' END'


