EXEC dbo.sp_executesql @statement = N' CREATE PROCEDURE dbo.rptInventoryCost @Date DATETIME AS BEGIN SET NOCOUNT ON;
SELECT L.Name, U.Name, COUNT( I.Cost ) AS BottleCount, SUM( I.Cost) as TotalCost FROM Inventories I JOIN UPCs U ON I.UPCID = U.UPCID JOIN Locations L ON I.LocationID = L.LocationID WHERE EnterDate < @Date AND ( ExitDate IS NULL OR ExitDate < @Date ) GROUP BY L.Name, U.Name ORDER BY L.Name, U.Name END'
